INSERT INTO `wp_posts` VALUES("1", "1", "2013-11-25 04:04:43", "2013-11-25 04:04:43", "Welcome to WordPress. This is your first post. Edit or delete it, then start blogging!", "Hello world!", "", "trash", "open", "open", "", "hello-world", "", "", "2013-11-25 04:48:39", "2013-11-25 04:48:39", "", "0", "http://abilitytripwp/?p=1", "0", "post", "", "1");
INSERT INTO `wp_posts` VALUES("2", "1", "2013-11-25 04:04:43", "2013-11-25 04:04:43", "This is an example page. It\'s different from a blog post because it will stay in one place and will show up in your site navigation (in most themes). Most people start with an About page that introduces them to potential site visitors. It might say something like this:\n\n<blockquote>Hi there! I\'m a bike messenger by day, aspiring actor by night, and this is my blog. I live in Los Angeles, have a great dog named Jack, and I like pi&#241;a coladas. (And gettin\' caught in the rain.)</blockquote>\n\n...or something like this:\n\n<blockquote>The XYZ Doohickey Company was founded in 1971, and has been providing quality doohickeys to the public ever since. Located in Gotham City, XYZ employs over 2,000 people and does all kinds of awesome things for the Gotham community.</blockquote>\n\nAs a new WordPress user, you should go to <a href=\"http://abilitytripwp/wp-admin/\">your dashboard</a> to delete this page and create new pages for your content. Have fun!", "Sample Page", "", "trash", "open", "open", "", "sample-page", "", "", "2013-12-22 16:35:44", "2013-12-22 16:35:44", "", "0", "http://abilitytripwp/?page_id=2", "0", "page", "", "1");
INSERT INTO `wp_posts` VALUES("4", "1", "2013-11-25 04:48:39", "2013-11-25 04:48:39", "Welcome to WordPress. This is your first post. Edit or delete it, then start blogging!", "Hello world!", "", "inherit", "open", "open", "", "1-revision-v1", "", "", "2013-11-25 04:48:39", "2013-11-25 04:48:39", "", "1", "http://abilitytripwp/?p=4", "0", "revision", "", "0");
INSERT INTO `wp_posts` VALUES("5", "1", "2013-11-25 05:04:25", "2013-11-25 05:04:25", "AbilityTrip is a centralized resource for accessible travel information – a travel guide – for the community of disabled travelers and their companions. The site provides information on the current state of accessibility by destination including logistics, accommodations, activities, restaurants, and emergency services. AbilityTrip also provides consulting services to destinations seeking to increase their utility and attractiveness to the disabled community.", "What is Ability Trip?", "", "publish", "open", "open", "", "what-is-ability-trip", "", "", "2013-11-25 05:24:05", "2013-11-25 05:24:05", "", "0", "http://abilitytripwp/?p=5", "0", "post", "", "0");
INSERT INTO `wp_posts` VALUES("6", "1", "2013-11-25 05:01:29", "2013-11-25 05:01:29", "", "Homepage letterbox", "Chicago", "inherit", "open", "open", "", "letterbox3", "", "", "2013-11-25 05:01:29", "2013-11-25 05:01:29", "", "5", "http://abilitytripwp/wp-content/uploads/2013/11/letterbox3.jpg", "0", "attachment", "image/jpeg", "0");
INSERT INTO `wp_posts` VALUES("7", "1", "2013-11-25 05:04:25", "2013-11-25 05:04:25", "AbilityTrip is a centralized resource for accessible travel information – a travel guide – for the community of disabled travelers and their companions. The site provides information on the current state of accessibility by destination including logistics, accommodations, activities, restaurants, and emergency services. AbilityTrip also provides consulting services to destinations seeking to increase their utility and attractiveness to the disabled community.", "What is Ability Trip?", "", "inherit", "open", "open", "", "5-revision-v1", "", "", "2013-11-25 05:04:25", "2013-11-25 05:04:25", "", "5", "http://abilitytripwp/?p=7", "0", "revision", "", "0");
INSERT INTO `wp_posts` VALUES("9", "1", "2013-11-25 05:11:34", "2013-11-25 05:11:34", "AbilityTrip is a centralized resource for accessible travel information – a travel guide – for the community of disabled travelers and their companions. The site provides information on the current state of accessibility by destination including logistics, accommodations, activities, restaurants, and emergency services. AbilityTrip also provides consulting services to destinations seeking to increase their utility and attractiveness to the disabled community.\r\n\r\n&nbsp;\r\n\r\naHello", "What is Ability Trip?", "", "inherit", "open", "open", "", "5-revision-v1", "", "", "2013-11-25 05:11:34", "2013-11-25 05:11:34", "", "5", "http://abilitytripwp/?p=9", "0", "revision", "", "0");
