INSERT INTO `wp_posts` VALUES("17", "1", "2013-11-25 05:23:33", "2013-11-25 05:23:33", "AbilityTrip is a centralized resource for accessible travel information – a travel guide – for the community of disabled travelers and their companions. The site provides information on the current state of accessibility by destination including logistics, accommodations, activities, restaurants, and emergency services. AbilityTrip also provides consulting services to destinations seeking to increase their utility and attractiveness to the disabled community.\r\n\r\n<p>hello</p>", "What is Ability Trip?", "", "inherit", "open", "open", "", "5-revision-v1", "", "", "2013-11-25 05:23:33", "2013-11-25 05:23:33", "", "5", "http://abilitytripwp/?p=17", "0", "revision", "", "0");
