INSERT INTO `wp_posts` VALUES("20", "1", "2013-11-24 05:27:04", "2013-11-24 05:27:04", "Join our free community today to learn about accessible features of destinations and share your experiences with fellow travelers. AbilityTrip relies on the experienced-based contributions of its members to help others make informed decisions about their accessible travel options.", "How can you get involved?", "", "publish", "open", "open", "", "how-can-you-get-involved", "", "", "2013-11-25 05:29:39", "2013-11-25 05:29:39", "", "0", "http://abilitytripwp/?p=20", "0", "post", "", "0");
